import React, { createContext, useContext, useState, useEffect } from 'react';
import apiClient from '../services/apiClient';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Check if user is already logged in
    const checkAuth = async () => {
      try {
        const token = localStorage.getItem('authToken');
        if (token) {
          apiClient.setToken(token); // Ensure API client has the token
          const userData = await apiClient.getCurrentUser();
          setUser(userData);
          setIsAuthenticated(true);
        }
      } catch (error) {
        console.error('Auth check failed:', error);
        // Clear invalid token
        localStorage.removeItem('authToken');
        apiClient.setToken(null);
        setUser(null);
        setIsAuthenticated(false);
      } finally {
        setLoading(false);
      }
    };

    checkAuth();
  }, []);

  const login = async (email, password) => {
    try {
      const response = await apiClient.login(email, password);
      setUser(response.user);
      setIsAuthenticated(true);
      return response;
    } catch (error) {
      console.error('Login failed:', error);
      throw error;
    }
  };

  const register = async (userData) => {
    try {
      const response = await apiClient.register(userData);
      return response;
    } catch (error) {
      console.error('Registration failed:', error);
      throw error;
    }
  };

  const sendOTP = async (email) => {
    try {
      const response = await apiClient.sendOTP(email);
      return response;
    } catch (error) {
      console.error('Send OTP failed:', error);
      throw error;
    }
  };

  const verifyOTP = async (email, otp) => {
    try {
      const response = await apiClient.verifyOTP(email, otp);
      return response;
    } catch (error) {
      console.error('OTP verification failed:', error);
      throw error;
    }
  };

  const resendOTP = async (email) => {
    try {
      const response = await apiClient.resendOTP(email);
      return response;
    } catch (error) {
      console.error('Resend OTP failed:', error);
      throw error;
    }
  };

  const logout = async () => {
    try {
      await apiClient.logout();
      setUser(null);
      setIsAuthenticated(false);
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  const loginWithGoogle = async () => {
    try {
      // Get Google OAuth URL from backend
      const response = await apiClient.getGoogleOAuthUrl();
      const { auth_url } = response;
      
      return new Promise((resolve, reject) => {
        let isResolved = false;
        let checkClosedInterval;
        let timeoutId;
        let oauthSuccess = false;

        // Listen for messages from the popup (sent by backend callback)
        const messageListener = (event) => {
          console.log('üîç Received message:', event.data, 'from origin:', event.origin);

          // Accept messages from our backend origin (both HTTP and HTTPS), and wildcard '*' for dev
          const allowedOrigins = ['http://localhost:8000', 'https://localhost:8000', window.location.origin, '*'];
          if (!allowedOrigins.includes(event.origin)) {
            console.log('üîç Ignoring message from unexpected origin:', event.origin);
            return;
          }

          if (isResolved) {
            console.log('üîç Message received but already resolved, ignoring');
            return; // Prevent multiple resolutions
          }

          isResolved = true;
          if (checkClosedInterval) {
            clearInterval(checkClosedInterval);
            checkClosedInterval = null;
          }
          if (timeoutId) {
            clearTimeout(timeoutId);
            timeoutId = null;
          }

          if (event.data.success && event.data.access_token) {
            oauthSuccess = true;
            console.log('‚úÖ OAuth success received:', event.data);
            // Backend successfully processed OAuth and sent us the token
            apiClient.setToken(event.data.access_token);
            setUser(event.data.user);
            setIsAuthenticated(true);

            window.removeEventListener('message', messageListener);
            try {
              if (popup && !popup.closed) popup.close();
            } catch (e) {
              console.warn('Could not close popup:', e);
            }
            resolve(event.data);
          } else if (event.data.error) {
            window.removeEventListener('message', messageListener);
            try {
              if (popup && !popup.closed) popup.close();
            } catch (e) {
              console.warn('Could not close popup:', e);
            }
            reject(new Error(event.data.error));
          } else {
            window.removeEventListener('message', messageListener);
            console.log('üîç Received message with unexpected format:', event.data);
          }
        };

        window.addEventListener('message', messageListener);

        // Open popup window for Google OAuth
        const popup = window.open(
          auth_url,
          'google-oauth',
          'width=500,height=600,scrollbars=yes,resizable=yes'
        );

        if (!popup) {
          window.removeEventListener('message', messageListener);
          reject(new Error('Failed to open OAuth popup. Please allow popups for this site.'));
          return;
        }

        // Cleanup function
        const cleanup = () => {
          window.removeEventListener('message', messageListener);
          if (checkClosedInterval) {
            clearInterval(checkClosedInterval);
            checkClosedInterval = null;
          }
          if (timeoutId) {
            clearTimeout(timeoutId);
            timeoutId = null;
          }
        };

        // Handle popup closure detection with COOP compatibility
        let popupCheckAttempts = 0;
        const maxPopupCheckAttempts = 3; // Minimal attempts to avoid console spam

        const checkClosed = () => {
          popupCheckAttempts++;

          try {
            // Try to access popup.closed, but handle COOP gracefully
            if (popup && popup.closed) {
              if (!isResolved && !oauthSuccess) {
                console.log('üîç Popup was closed by user (attempt', popupCheckAttempts, ')');
                // Wait 500ms before rejecting, to allow any pending message events
                setTimeout(() => {
                  if (!isResolved && !oauthSuccess) {
                    isResolved = true;
                    cleanup();
                    reject(new Error('OAuth popup was closed by user'));
                  }
                }, 500);
              }
              return;
            } else {
              console.log('üîç Popup still open (attempt', popupCheckAttempts, ')');
            }
          } catch (error) {
            // COOP policy blocks access - this is expected in secure contexts
            // Don't log this as an error since it's normal behavior
            if (popupCheckAttempts === 1) {
              console.log('üîç COOP policy active - relying on message listener for popup status');
            }
          }

          // After a few failed attempts, stop trying to check popup.closed
          if (popupCheckAttempts >= maxPopupCheckAttempts) {
            if (checkClosedInterval) {
              clearInterval(checkClosedInterval);
              checkClosedInterval = null;
            }
            console.log('üîç Stopped checking popup.closed, relying on message listener');
            // Continue relying on message listener and timeout
          }
        };

        // Start checking popup status with longer intervals to reduce COOP errors
        checkClosedInterval = setInterval(checkClosed, 3000); // Check every 3 seconds

        // Set a reasonable timeout for OAuth process
        timeoutId = setTimeout(() => {
          if (!isResolved) {
            console.log('üîç OAuth timeout reached');
            isResolved = true;
            cleanup();
            try {
              if (popup && !popup.closed) popup.close();
            } catch (e) {
              console.warn('Could not close popup on timeout:', e);
            }
            reject(new Error('OAuth timeout - please ensure you accept the SSL certificate and try again'));
          }
        }, 300000); // 5 minutes to allow for certificate acceptance and debugging
      });
    } catch (error) {
      console.error('Google OAuth failed:', error);
      throw error;
    }
  };

  const value = {
    user,
    isAuthenticated,
    loading,
    login,
    register,
    logout,
    loginWithGoogle,
    sendOTP,
    verifyOTP,
    resendOTP,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
